import { REQUEST_TYPES_COIN,
  RECEIVE_TYPES_COIN_FAILURE,
  RECEIVE_TYPES_COIN_SUCESS,
  SET_EMAIL, } from '../actions/actionTypes';

const INITIAL_STATE = {
  currencies: [],
  expenses: [],
  editor: false,
  idToEdit: 0,
  isFetching: false,
  error: '',
};

const walletReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case REQUEST_TYPES_COIN:
    return {
      ...state,
      isFetching: true,
    };
  case RECEIVE_TYPES_COIN_FAILURE:
    return {
      ...state,
      error: action.error,
      isFetching: false,
    };
  case RECEIVE_TYPES_COIN_SUCESS:
    return {
      ...state,
      currencies: action.coins,
      isFetching: false,
    };
  default: return state;
  }
};

export default walletReducer;
