import React from 'react';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
    };
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  };

  render() {
    return (
      <main>
        <section>
          <input
            type="email" 
            name="input-email" onChange={ this.handleChange } data-testid="email-input" />
          <input type="text" name="input-password" data-testid="password-input" />
        </section>
        <section>
          <button type="button" disabled>Entrar</button>
        </section>
      </main>
    );
  }
}

export default Login;
