import SET_EMAIL from '../actions/actionTypes';

const INITIAL_STATE = {
  user: {
    email: '', // string que armazena o email da pessoa usuária
  },
};

const userReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SET_EMAIL:
    return {
      ...state,
      user: {
        email: action.email,
      },
    };
  default:
    return state;
  }
};

export default userReducer;
