import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import saveEmail from '../redux/actions/index';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
      btnIsDisable: true,
    };
  }

  handleButton = () => {
    const { email, password } = this.state;
    const minLengthPassword = 5;
    const emailIsValid = /^[\w+.]+@\w+\.\w{2,}(?:\.\w{2})?$/.test(email);
    const passwordIsValid = password.length > minLengthPassword;
    const result = regex && passwordIsValid;
    this.setState({
      btnIsDisable: !result,
    });
  };

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    }, () => this.handleButton());
  };

  handleSubmit = (event) => {
    event.preventDefault();

    const { email } = this.state;
    const { dispatch } = this.props;
    dispatch(saveEmail(email));

    const { history } = this.props;
    history.push('/carteira');
  };

  render() {
    const { email, password, btnIsDisable } = this.state;
    return (
      <form>
        <input
          type="email"
          name="email"
          onChange={ this.handleChange }
          data-testid="email-input"
          value={ email }
        />
        <input
          type="password"
          name="password"
          onChange={ this.handleChange }
          value={ password }
          data-testid="password-input"
        />
        <button
          type="submit"
          onClick={ this.handleSubmit }
          disabled={ btnIsDisable }
        >
          Entrar
        </button>
      </form>
    );
  }
}

Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func.isRequired,
  }).isRequired,
  dispatch: PropTypes.func.isRequired,
};

const mapStateToProps = (store) => ({
  email: store.user.email,
});

export default connect(mapStateToProps)(Login);
