import { SET_EMAIL, SET_CURRENCIES } from '../actions/actionTypes';

const INITIAL_STATE = {
  email: '',
};

const userReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SET_EMAIL:
    return {
      ...state,
      email: action.email,
    };
  case SET_CURRENCIES:
  default:
    return state;
  }
};

export default userReducer;
