import { REQUEST_TYPES_COIN, SET_CURRENCIES } from '../actions/actionTypes';

const INITIAL_STATE = {
  currencies: [],
  expenses: [],
  editor: false,
  idToEdit: 0,
  isFetching: false,
};

const walletReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  // case SET_CURRENCIES:
  //   return {
  //     ...state,
  //     currencies: action.coins,
  //   };
  case REQUEST_TYPES_COIN:
    return {
      ...state,
      isFetching: true,
    };
  default: return state;
  }
};

export default walletReducer;
