import React from 'react';

class Login extends React.Component {


  handleChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  };

  render() {
    return (
      <main>
        <section>
          <input type="email" name="input-email" data-testid="email-input" />
          <input type="text" name="input-password" data-testid="password-input" />
        </section>
        <section>
          <button type="button" disabled>Entrar</button>
        </section>
      </main>
    );
  }
}

export default Login;
