import SET_EMAIL from '../actions/actionTypes';

const INITIAL_STATE = {
  email: '',
    currencies: [],
    expenses: [],
    editor: false,
    idToEdit: 0,
};

const userReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case SET_EMAIL:
    return {
      ...state,
      user: {
        email: action.email,
      },
    };
  default:
    return state;
  }
};

export default userReducer;
