import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { decreaseValue, deleteExpense } from '../redux/actions';

class Table extends Component {
  handleClick = ({ target }) => {
    const { expenses, dispatch } = this.props;
    const newExpenses = expenses.filter(({ id }) => (id !== Number(target.id)));
    dispatch(deleteExpense(newExpenses));
    const oi = newExpenses.reduce((cur, acc) => {
      const act = parseFloat(Number(cur.value) * Number(cur.exchangeRates[currency].ask)).toFixed(2);
      return act + acc;
    }, 0);
  };

  render() {
    const { expenses } = this.props;
    return (
      <table>
        <thead>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Moeda</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
        </thead>
        <tbody>
          { expenses.map(
            ({ id, description, tag, method, value, exchangeRates, currency }) => (
              <tr key={ id }>
                <td>{ description }</td>
                <td>{ tag }</td>
                <td>{ method }</td>
                <td>{ Number(value).toFixed(2) }</td>
                <td>{ exchangeRates[currency].name }</td>
                <td>{ parseFloat(exchangeRates[currency].ask).toFixed(2) }</td>
                <td>{ (exchangeRates[currency].ask * value).toFixed(2) }</td>
                <td>Real</td>
                <td>
                  <button type="button">Editar</button>
                  <button
                    type="button"
                    data-testid="delete-btn"
                    id={ id }
                    onClick={ this.handleClick }
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ),
          ) }
        </tbody>
      </table>
    );
  }
}

const mapStateToProps = (store) => ({
  expenses: store.wallet.expenses,
});

Table.propTypes = {
  expenses: PropTypes.arrayOf(String).isRequired,
};

export default connect(mapStateToProps)(Table);
