import React, { Component } from 'react';
import { connect } from 'react-redux';
import '../mainStyle.css';
import { setCurrencies } from '../redux/actions';
import fetchApi from '../services/fetchApi';

class WalletForm extends Component {
  async componentDidMount() {
    const { dispatch } = this.props;

    const coins = await fetchApi();
    const coinsValues = Object.values(coins);
    const filteredCoins = coinsValues.filter((coin) => (coin.codein !== 'BRLT'
      ? coin : ''));
    dispatch(setCurrencies(filteredCoins));
  }

  render() {
    return (
      <div className="main">
        <input data-testid="value-input" />
        <input data-testid="description-input" />
        <select id="currencies" data-testid="currency-input">

          <option id="currency" />
        </select>
        <input data-testid="method-input" />
        <input data-testid="tag-input" />
        <button type="button" />
      </div>
    );
  }
}

const mapStateToProps = (store) => ({
  currencies: store.wallet.currencies,
});

export default connect(mapStateToProps)(WalletForm);
