import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import '../mainStyle.css';
import { setCurrencies } from '../redux/actions';
import fetchApi from '../services/fetchApi';

class WalletForm extends Component {
  async componentDidMount() {
    const { dispatch } = this.props;

    const coins = await fetchApi();
    const coinsKeys = Object.keys(coins);
    const filteredCoins = coinsKeys.filter((coin) => (coin !== 'USDT'
      ? coin : ''));
    dispatch(setCurrencies(filteredCoins));
  }

  render() {
    const { currencies } = this.props;
    const tags = ['Alimentação', 'Lazer', 'Trabalho', 'Transporte', 'Saúde'];
    return (
      <div className="main">
        <input type="number" data-testid="value-input" />
        <input type="text" data-testid="description-input" />

        <label htmlFor="currencies">
          <select
            data-testid="currency-input"
            id="currencies"
          >
            { currencies.map((coin, index) => (
              <option
                id="currencies"
                key={ index }
                value={ coin }
              >
                { coin }
              </option>
            ))}
          </select>
        </label>

        <label htmlFor="pay-method">
          <select
            data-testid="method-input"
            id="pay-method"
          >
            <option
              id="pay-method"
              value="Dinheiro"
            >
              Dinheiro
            </option>
            <option
              id="pay-method"
              value="Cartão de crédito"
            >
              Cartão de crédito
            </option>
            <option
              id="pay-method"
              value="Cartão de débito"
            >
              Cartão de débito
            </option>
          </select>
        </label>

        <label htmlFor="tag">
          <select
            data-testid="tag-input"
            id="tag"
          >
            { tags.map((tag) => (
              <option
                id="tag"
                key={ tag }
                value={ tag }
              >
                { tag }
              </option>
            ))}
          </select>
        </label>
        <button type="button">Adicionar despesa</button>
      </div>
    );
  }
}

const mapStateToProps = (store) => ({
  currencies: store.wallet.currencies,
});

WalletForm.propTypes = {
  dispatch: PropTypes.func.isRequired,
  currencies: PropTypes.shape({
    map: PropTypes.func.isRequired,
  }).isRequired,
};

export default connect(mapStateToProps)(WalletForm);
